std::string markdown_to_html(std::string &markdown_text) {
    std::regex html_regex("<([^<>]*)>");
    std::string html_text = std::regex_replace(markdown_text, html_regex, "$1");

    // Convirtiendo links
    std::regex link_regex("(\\[([^]]*)\\]\\(([^]]*)\\))");
    std::smatch link_match;
