#include <iostream>
#include <string>
#include <regex>

std::string markdown_to_html(std::string markdown_text) {
    std::regex markdown_regex(".*");
    std::regex html_regex(".*");

    std::smatch match;

    while (std::regex_search(markdown_text, match, markdown_regex)) {
        // Replace markdown with html
        markdown_text.replace(match.position(), match.length(), regex_replace(match.str(), html_regex));
    }

    return markdown_text;
}

int main() {
    std::string markdown_text = "This is a **bold** text";
    std::string html_text = markdown_to_html(markdown_text);

    std::cout << "Markdown: " << markdown_text << "\nHTML: " << html_text << std::endl;

    return 0;
}

