std::string convertMarkdownToHtml(const std::string& markdownText) {
    std::string htmlText;

    // On crée une expression régulière pour les balises markdown
    const std::regex markdownRegex("\\[(.*?)\\]\\((.*?)\\)");

    // On parcourt les groupes de la balise markdown
    std::smatch matches;
    while (std::regex_search(markdownText, matches, markdownRegex)) {
