std::string convertMarkdownToHTML(std::string& markdown) {
    // Convertir les balises de titre en HTML
    markdown = boost::replace_all(markdown, "##", "<h2>");
    markdown = boost::replace_all(markdown, "###", "<h3>");
    markdown = boost::replace_all(markdown, "####", "<h4>");
    markdown = boost::replace_all(markdown, "#####", "<h5>");
    markdown = boost::replace_all(markdown, "######", "<h6>");

    // Convertir les liens en HTML
