    markdown = boost::replace_all(markdown, "]", "\"></a>");

    // Convertir les italiques en HTML
    markdown = boost::replace_all(markdown, "*", "<em>");
    markdown = boost::replace_all(markdown, "*", "</em>");

    // Convertir les gras en HTML
    markdown = boost::replace_all(markdown, "__", "<strong>");
    markdown = boost::replace_all(markdown, "__", "</strong>");

    // Convertir les listes non numérotées en HTML
    std::vector<std::string> lines;
    split(lines, markdown, is_any_of("\n"));
    for (int i = 0; i < lines.size(); i++) {
        if (lines[i].substr(0, 2) == "- ") {
            lines[i] = "<li>" + lines[i].substr(2) + "</li>";
        }
    }
    markdown = join(lines, "\n");

    return markdown;
}

