std::string markdown_to_html(const std::string& markdown) {
    std::vector<std::string> tokens = split(markdown, '\n');
    std::string html;
    for (auto& token : tokens) {
        if (token.empty()) continue;
        if (token[0] == '#') {
            html += "<h" + std::to_string(token.size() - 1) + ">" + token.substr(2) + "</h" + std::to_string(token.size() - 1) + ">";
        } else if (token[0] == '-') {
            html += "<ul>";
            for (auto& item : split(token.substr(2), ', ')) {
                html += "<li>" + item + "</li>";
            }
            html += "</ul>";
        } else if (token[0] == '*') {
            html += "<ul>";
            for (auto& item : split(token.substr(2), ', ')) {
                html += "<li>" + item + "</li>";
            }
            html += "</ul>";
        } else {
            html += token;
        }
    }
    return html;
}
```

