```c++
#include <iostream>
#include <string>
#include <vector>
#include <algorithm>
using namespace std;

// Function to convert markdown to HTML
string convertMarkdownToHTML(string markdown) {
    // Replace new line character with <br>
    markdown = replace(markdown.begin(), markdown.end(), '\n', '<br>');

    // Replace * with <i>
    markdown = replace(markdown.begin(), markdown.end(), '*', '<i>');

    // Replace _ with <b>
    markdown = replace(markdown.begin(), markdown.end(), '_', '<b>');

    return markdown;
}

int main() {
    string markdown = "This is a #markdown# text. It's a simple *markdown* syntax that uses *italics* and **bold** for emphasis. It also supports code blocks like this: \n\n```cpp\n#include <iostream>\nusing namespace std;\n\nint main() {\n    cout << "Hello, World!" << endl;\n    return 0;\n}\n```\n";

    string html = convertMarkdownToHTML(markdown);

    cout << "HTML version of the markdown text: " << endl;
    cout << html << endl;

    return 0;
}
```

