class Stack:
    def __init__(self):
        self.items = []

    def push(self, item):
        self.items.append(item)

    def pop(self):
        return self.items.pop()

    def __repr__(self):
        return f"{self.items}"

class Queue:
    def __init__(self):
        self.items = []
        self.stack1 = Stack()
        self.stack2 = Stack()

    def push(self, item):
        self.stack1.push(item)

    def pop(self):
        if len(self.stack2.items) == 0:
            while len(self.stack1.items) > 0:
                self.stack2.push(self.stack1.pop())
        return self.stack2.pop()

    def __repr__(self):
        return f"{self.stack1}, {self.stack2}"
```

