std::string convertMarkdownToHTML(const std::string& markdown) {
    std::stringstream ss;
    std::vector<std::string> lines;
    std::string line;

    // Split input into lines
    while (std::getline(markdown, line)) {
        lines.push_back(line);
    }

    // Process each line
    for (const auto& line : lines) {
        // Check for header
        if (line[0] == '#') {
            // Convert to h1-h6 tags
            int level = line[0] - '0';
            ss << "<h" << level << ">" << line.substr(1) << "</h" << level << ">\n";
        } else if (line[0] == '*') {
            // Convert to unordered list items
            ss << "<li>" << line.substr(1) << "</li>\n";
        } else if (line[0] == '-') {
            // Convert to ordered list items
            ss << "<li>" << line.substr(1) << "</li>\n";
        } else if (line[0] == '>') {
            // Convert to blockquote
            ss << "<blockquote>" << line.substr(1) << "</blockquote>\n";
        } else if (line[0] == '`') {
            // Convert to code block
            ss << "<pre><code>" << line.substr(1) << "</code></pre>\n";
        } else if (line.find('[') != std::string::npos && line.find(']') != std::string::npos) {
            // Convert to link
            auto start = line.find('[');
            auto end = line.find(']');
            auto url = line.substr(start + 1, end - start - 1);
            ss << "<a href=\"" << url << "\">" << line.substr(start + 2, end - start - 2) << "</a>";
        } else {
            // Add regular text
            ss << line;
        }
    }

    return ss.str();
}

int main() {
    std::string markdown = "## Hello World\n\n> This is a quote.\n\n- Item 1\n- Item 2";

    std::cout << convertMarkdownToHTML(markdown);
}
```

