#include <string>
#include <sstream>
#include <iostream>

std::string toHtml(const std::string& input) {
    std::stringstream output;
    output << "<p>";
    size_t pos = 0;
    while ((pos = input.find("*", pos)) != std::string::npos) {
        output << input.substr(pos, 1);
        output << "<em>";
        pos += 1;
        while ((pos = input.find("*", pos)) != std::string::npos) {
            output << input.substr(pos, 1);
            pos += 1;
        }
        output << "</em>";
    }
    output << input;
    output << "</p>";
    return output.str();
}

int main() {
    std::string input = "Здравствуйте, *world*";
    std::cout << toHtml(input);
}
```
