```c++
#include <string>
#include <iostream>
#include <vector>
#include <regex>
#include <algorithm>

std::string convert_markdown_to_html(const std::string& markdown) {
    std::vector<std::string> markdown_lines = split_string(markdown, "\n");
    std::vector<std::string> html_lines;

    for (const auto& line : markdown_lines) {
        // Replace markdown headers with HTML headers
        if (regex_match(line, regex("^#.*$"))) {
            std::string header_level = regex_replace(line, regex("^#"), "<h");
            header_level += ">";

            // Add the header level to the line
            line = header_level + regex_replace(line, regex("^#"), "") + "</" + header_level + ">";

        } else if (regex_match(line, regex("^### .*$"))) {
            std::string header_level = regex_replace(line, regex("^###"), "<h3>");
            header_level += ">";

            // Add the header level to the line
            line = header_level + regex_replace(line, regex("^###"), "") + "</" + header_level + ">";

        } else if (regex_match(line, regex("^## .*$"))) {
            std::string header_level = regex_replace(line, regex("^##"), "<h2>");
            header_level += ">";

            // Add the header level to the line
            line = header_level + regex_replace(line, regex("^##"), "") + "</" + header_level + ">";

        } else if (regex_match(line, regex("^.*$"))) {
            // Replace markdown links with HTML links
            std::string link = regex_replace(line, regex("(.*)\[([^\]]*)\]\((.*)\)", "$1<a href=\"$3\">$2</a>");

            // Add the link to the line
            line = link;
        }

        html_lines.push_back(line);
    }

    return join_string(html_lines, "\n");
}

std::vector<std::string> split_string(const std::string& s, const std::string& delim) {
    std::vector<std::string> tokens;
    size_t prev = 0, pos = 0;
    do {
        pos = s.find(delim, prev);
        if (pos == std::string::npos) pos = s.length();
        std::string token = s.substr(prev, pos - prev);
        tokens.push_back(token);
        prev = pos + delim.length();
    }
    while (pos < s.length() && prev < s.length());
    return tokens;
}

std::string join_string(const std::vector<std::string>& s, const std::string& delim) {
    return boost::algorithm::join(s, delim);
}
```

Det här är en generell funktion som kan användas för att konvertera alla typer av markdown-element till deras motsvarande HTML-element. Den använder sig av regular expressions för att ersätta de olika markdown-elementen med deras HTML-korrespondenser.

