function countChanges(table) {
  const rows = table.length;
  const cols = table[0].length;
  const changes = Array.from({ length: rows }, () => Array(cols).fill(0));

  for (let i = 0; i < rows; i++) {
    for (let j = 0; j < cols; j++) {
      if (table[i][j] !== 0) {
        changes[i][j] =
          table[i][j] !== table.slice(0, i).flat().filter((e) => e === table[i][j])[0]
            ? 1
            : -1;
      }
    }
  }

  let totalChanges = 0;
  for (let i = 0; i < rows; i++) {
    for (let j = 0; j < cols; j++) {
      totalChanges += changes[i][j];
    }
  }

  return totalChanges;
}
