public int countChanges(int[][] table) {
    int rows = table.length;
    int cols = table[0].length;
    int[][] changes = new int[rows][cols];

    for (int i = 0; i < rows; i++) {
        for (int j = 0; j < cols; j++) {
            if (table[i][j] != 0) {
                boolean foundInPreviousRows = false;
                for (int k = 0; k < i; k++) {
                    if (table[k][j] == table[i][j]) {
                        foundInPreviousRows = true;
                        break;
                    }
                }

                changes[i][j] =
                        foundInPreviousRows ? -1 : table[i][j] != table.slice(0, i).flat().filter((e) => e === table[i][j])[0] ? 1 : -1;
            }
        }
    }

    int totalChanges = 0;
    for (int i = 0; i < rows; i++) {
        for (int j = 0; j < cols; j++) {
            totalChanges += changes[i][j];
        }
    }

    return totalChanges;
}
