            if s1[i-1] == s2[j-1]:
                c[i][j] = c[i-1][j-1] + 1
            else:
                c[i][j] = max(c[i-1][j], c[i][j-1])
    return c[m][n]

def lcs2(s1, s2):
    res = []
    m = len(s1)
    n = len(s2)
    i, j = m, n
    while i > 0 and j > 0:
