#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
    // Abrir el archivo de texto
    string fileName;
    cout << "Ingrese el nombre del archivo: ";
    cin >> fileName;
    ifstream inputFile(fileName);

    // Leer la palabra a buscar
    string wordToSearch;
    cout << "Ingrese la palabra a buscar: ";
    cin >> wordToSearch;

    // Contar el número de apariciones de la palabra en el archivo
    int wordCount = 0;
    string line;
    while (getline(inputFile, line)) {
        size_t pos = line.find(wordToSearch);
        while (pos != string::npos) {
            wordCount++;
            pos = line.find(wordToSearch, pos + 1);
        }
    }

    // Mostrar el resultado
    cout << "La palabra '" << wordToSearch << "' aparece " << wordCount << " veces en el archivo." << endl;

    return 0;
}
```

