```c++
#include <string>
#include <iostream>

std::string convertMarkdownToHtml(const std::string& markdownText) {
    // Create a temporary file to store the converted text
    std::FILE* tempFile = std::tmpfile();

    // Write the markdown text to the temporary file
    std::fwrite(markdownText.c_str(), 1, markdownText.size(), tempFile);

    // Close the temporary file
    std::fclose(tempFile);

    // Create a command to run Pandoc on the temporary file
    std::string command = "pandoc --from=markdown --to=html --output=-";

    // Run the command and capture its output
    std::array<char, 128> buffer;
    std::unique_ptr<FILE, decltype(&std::pclose)> pipe(popen(command.c_str(), "r"), &std::pclose);
    if (!pipe) {
        throw std::runtime_error("popen() failed!");
    }
    while (fgets(buffer.data(), buffer.size(), pipe.get()) != nullptr) {
        // Do nothing with the output
    }

    // Read the contents of the temporary file and convert it to a string
    std::string htmlText;
    std::rewind(tempFile);
    while (!std::feof(tempFile)) {
        htmlText += static_cast<char>(std::fgetc(tempFile));
    }

    return htmlText;
}
```

