def find_longest_common_subsequence(string1, string2):
    # Lager to matricer for at gemme resultaterne
    dp = [[0] * (len(string2) + 1) for _ in range(len(string1) + 1)]

    # Itererer gennem hver linje og kolonne i matricen
    for i in range(1, len(string1) + 1):
        for j in range(1, len(string2) + 1):
            # Hvis de to tegn er ens, legger vi et i til den nuværende værdi
            if string1[i - 1] == string2[j - 1]:
                dp[i][j] = dp[i - 1][j - 1] + 1
            # Hvis de to tegn ikke er ens, viser vi den nuværende værdi
            else:
                dp[i][j] = max(dp[i - 1][j], dp[i][j - 1])

    # Finder den længste fælles delsekvens ved at gå gennem den sidste linje og kolonne i matricen
    longest_subsequence = []
    i, j = len(string1), len(string2)
    while i > 0 and j > 0:
        if string1[i - 1] == string2[j - 1]:
            longest_subsequence.append(string1[i - 1])
            i -= 1
            j -= 1
        elif dp[i - 1][j] > dp[i][j - 1]:
            i -= 1
        else:
            j -= 1

    return ''.join(reversed(longest_subsequence))
