def find_longest_common_subsequence(string1, string2):
    # Initialize a 2D array to store the length of the longest common subsequence
    # for each pair of prefixes in string1 and string2
    dp = [[0] * (len(string2) + 1) for _ in range(len(string1) + 1)]

    # Fill in the first row and column of the dp array
    for i in range(1, len(string1) + 1):
        dp[i][0] = 0
    for j in range(1, len(string2) + 1):
        dp[0][j] = 0

    # Fill in the rest of the dp array using dynamic programming
    for i in range(1, len(string1) + 1):
        for j in range(1, len(string2) + 1):
            if string1[i - 1] == string2[j - 1]:
                dp[i][j] = dp[i - 1][j - 1] + 1
            else:
                dp[i][j] = max(dp[i - 1][j], dp[i][j - 1])

    # Find the length of the longest common subsequence by tracing back through the dp array
    i = len(string1)
    j = len(string2)
    longest_common_subsequence = []
    while i > 0 and j > 0:
        if string1[i - 1] == string2[j - 1]:
            longest_common_subsequence.append(string1[i - 1])
            i -= 1
            j -= 1
        elif dp[i - 1][j] > dp[i][j - 1]:
            i -= 1
        else:
            j -= 1

    return longest_common_subsequence[::-1]
