```javascript
function isPrimeNumber(arr) {
  for (let i = 0; i < arr.length; i++) {
    let number = arr[i];
    if (isPrime(number)) {
      console.log(`Die Zahl ${number} ist eine Primzahl.`);
      return true;
    }
  }
  console.log(`Keine der Zahlen ist eine Primzahl.`);
  return false;
}

function isPrime(n) {
  // Die Zahl eins ist keine Primzahl.
  if (n === 1) {
    return false;
  }
  // Alle ungeraden Zahlen sind Primzahlen.
  if (n === 2) {
    return true;
  }
  // Alle geraden Zahlen größer als zwei sind keine Primzahlen.
  if (n % 2 === 0) {
    return false;
  }

  // Alle Primzahlen größer als zwei sind entweder ungerade oder teilerfähig durch ein gerades Quadrat.
  for (let i = 3; i <= Math.sqrt(n); i += 2) {
    if (n % i === 0) {
      return false;
    }
  }

  return true;
}
```

