#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
  string wordToCount; // Das Wort, nach dem gezählt werden soll
  cout << "Geben Sie das Wort ein, nach dem gezählt werden soll: ";
  cin >> wordToCount;

  string line; // Eine Zeile aus der Datei
  int wordCount = 0; // Die Anzahl der Vorkommnisse des Wortes
  ifstream infile("datei.txt"); // Öffnen der Datei
  if (infile.is_open()) {
    while (getline(infile, line)) {
      size_t pos = line.find(wordToCount);
      while (pos != string::npos) {
        wordCount++;
        pos = line.find(wordToCount, pos + wordToCount.length());
      }
    }
    infile.close();
  } else {
    cout << "Die Datei konnte nicht geöffnet werden." << endl;
    return 1;
  }

  cout << "Das Wort '" << wordToCount << "' kommt " << wordCount << " mal in der Datei vor." << endl;
  return 0;
}

