#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
  string word; // Wort, nach dem gesucht werden soll
  int count = 0; // Anzahl der Vorkommnisse des Worts

  // Eingabe des Suchworts
  cout << "Geben Sie das Wort ein, nach dem gesucht werden soll: ";
  cin >> word;

  // Öffnen der Textdatei
  ifstream file("file.txt");

  // Zeilenweise Lesen der Datei
  while (file >> word) {
    // Prüfen, ob das aktuelle Wort mit dem Suchwort übereinstimmt
    if (word == word) {
      count++;
    }
  }

  // Schließen der Datei
  file.close();

  // Ausgabe der Anzahl der Vorkommnisse des Worts
  cout << "Das Wort '" << word << "' kommt " << count << " mal vor." << endl;

  return 0;
}

