// Función para convertir texto formateado en Markdown a HTML
std::string convertMarkdownToHtml(std::string& markdownText) {
    // Crea un objeto stringstream para almacenar el texto formateado en HTML
    std::stringstream htmlStream;

    // Define un vector para almacenar las líneas del texto formateado en Markdown
    std::vector<std::string> markdownLines;

    // Separa el texto formateado en Markdown por líneas
    std::istringstream markdownStream(markdownText);
    std::string line;
    while (std::getline(markdownStream, line)) {
        markdownLines.push_back(line);
    }

    // Itera sobre cada línea del texto formateado en Markdown
    for (auto& line : markdownLines) {
        // Si la línea contiene un encabezado, lo convierte a HTML
        if (line[0] == '#') {
            htmlStream << "<h" << (line[1] - '0') << ">" << line.substr(2) << "</h" << (line[1] - '0') << ">\n";
        }
        // Si la línea contiene un enlace, lo convierte a HTML
        else if (line[0] == '[' && line[line.length() - 1] == ']') {
            int index = line.find(']');
            htmlStream << "<a href=\"" << line.substr(index + 2, line.find('"', index + 2) - index - 2) << "\">" << line.substr(1, index - 1) << "</a>";
        }
        // Si la línea contiene una lista desordenada, la convierte a HTML
        else if (line[0] == '-') {
            int index = line.find(' ');
            htmlStream << "<ul>\n<li>" << line.substr(index + 1) << "</li>\n";
            while (markdownStream.tellg() != std::streampos(-1) && markdownStream.peek() == '-') {
                std::getline(markdownStream, line);
                index = line.find(' ');
                htmlStream << "<li>" << line.substr(index + 1) << "</li>\n";
            }
            htmlStream << "</ul>";
        }
        // Si la línea contiene una lista ordenada, la convierte a HTML
        else if (line[0] == '1.' || line[0] == '2.' || line[0] == '3.') {
            int index = line.find('.');
            htmlStream << "<ol>\n<li>" << line.substr(index + 1) << "</li>\n";
            while (markdownStream.tellg() != std::streampos(-1) && markdownStream.peek() != '\n') {
                std::getline(markdownStream, line);
                index = line.find('.');
                htmlStream << "<li>" << line.substr(index + 1) << "</li>\n";
            }
            htmlStream << "</ol>";
        }
        // Si la línea contiene código fuente, lo convierte a HTML
        else if (line[0] == '`') {
            htmlStream << "<pre><code>" << line.substr(1, line.length() - 2) << "</code></pre>";
        }
        // Si la línea contiene un bloque de código, lo convierte a HTML
        else if (line[0] == '```') {
            int index = line.find('```');
            htmlStream << "<pre><code>" << line.substr(3, index - 3) << "</code></pre>";
        }
        // Si la línea contiene texto sin formato, lo agrega al flujo de salida como texto sin formato
        else {
            htmlStream << line + "\n";
        }
    }

    // Devuelve el texto formateado en HTML
    return htmlStream.str();
}

