  std::string html;

  // Replace markdown headers with HTML headers
  html = replace_all(markdown, "##", "<h2>");
  html = replace_all(html, "###", "<h3>");
  html = replace_all(html, "####", "<h4>");
  html = replace_all(html, "#####", "<h5>");
