    \draw (0,\y) rectangle (8,\y);
}
% Place the pieces
\def\rank{8} % row number
\def\file{a} % column letter
\foreach \piece in {R,B,K,Q,B,N,P,r,b,k,q,b,n,p} {
    \foreach \row in {1,...,8} {
        \ifthenelse{\equal{\piece}{R}}{
            \draw (\file,\rank) node {\piece};
            \advance\rank by -1;
        }{
            \ifthenelse{\equal{\piece}{B}}{
                \draw (\file,\rank) node {\piece};
                \advance\rank by -1;
            }{
                \ifthenelse{\equal{\piece}{K}}{
                    \draw (\file,\rank) node {\piece};
                    \advance\rank by -1;
                }{
