                        \draw (\file,\rank) node {\piece};
                        \advance\rank by -1;
                    }{
                        \ifthenelse{\equal{\piece}{B}}{
                            \draw (\file,\rank) node {\piece};
                            \advance\rank by -1;
                        }{
                            \ifthenelse{\equal{\piece}{N}}{
                                \draw (\file,\rank) node {\piece};
                                \advance\rank by -1;
                            }{
                                \ifthenelse{\equal{\piece}{P}}{
                                    \draw (\file,\rank) node {\piece};
                                    \advance\rank by -1;
                                }{
                                    % blank square
