```c++
#include <string>
#include <iostream>
#include <regex>

std::string convertToHtml(const std::string& markdown) {
  std::regex italicRegex("\\*(.*?)\\*");
  std::regex boldRegex("\\**\\1\\**");

  std::string html = "";
  for (const auto& line : split(markdown, "\n")) {
    html += "<p>";

    for (const auto& match : regex_all(line, italicRegex)) {
      html += "<i>" + match + "</i>";
    }

    for (const auto& match : regex_all(line, boldRegex)) {
      html += "<b>" + match + "</b>";
    }

    html += "</p>";
  }

  return html;
}

std::vector<std::string> split(const std::string& s, const std::string& delim) {
  size_t pos_start = 0, pos_end, delim_len = delim.length();
  std::vector<std::string> res;

  while ((pos_end = s.find(delim, pos_start)) != std::string::npos) {
    res.push_back(s.substr(pos_start, pos_end - pos_start));
    pos_start = pos_end + delim_len;
  }

  res.push_back(s.substr(pos_start));
  return res;
}

int main() {
  const std::string markdown = "This is some **bold** *italic* text.";
  const std::string html = convertToHtml(markdown);

  std::cout << html << std::endl;

  return 0;
}
```

