        self.in_stack = []
        self.out_stack = []

    def enqueue(self, element):
        self.in_stack.append(element)

    def dequeue(self):
        if not self.out_stack:
            while self.in_stack:
                self.out_stack.append(self.in_stack.pop())
        return self.out_stack.pop()

    def is_empty(self):
        return not self.in_stack and not self.out_stack

    def print(self):
        print("In stack:", end=" ")
        for elem in self.in_stack:
            print(elem, end=" ")
        print()
        print("Out stack:", end=" ")
        for elem in self.out_stack:
            print(elem, end=" ")
