```c++
#include <iostream>
#include <string>
#include <sstream>
#include <vector>

std::string convert_markdown_to_html(const std::string& markdown) {
  std::stringstream ss;
  std::vector<std::string> lines;
  std::string line;

  // Split the markdown into lines
  for (char c : markdown) {
    if (c == '\n') {
      lines.push_back(line);
      line.clear();
    } else {
      line.push_back(c);
    }
  }
  lines.push_back(line);

  // Convert each line to html
  for (auto& line : lines) {
    if (line == "") continue;

    // Handle headers
    if (line[0] == '#') {
      ss << "<h" << (line.length() - 1) << ">" << line.substr(2) << "</h" << (line.length() - 1) << ">";
      continue;
    }

    // Handle bold and italic text
    std::stringstream ss_bold;
    std::stringstream ss_italic;
    bool bold = false;
    bool italic = false;
    for (char c : line) {
      if (c == '*' && !bold) {
        italic = !italic;
        if (!italic) ss_italic << c;
        else ss_bold << c;
      } else if (!bold && !italic) {
        ss_bold << c;
      } else {
        if (bold) ss_bold << c;
        if (italic) ss_italic << c;
      }
      if (!bold && !italic && c == '*') italic = !italic;
    }
    if (bold) ss_bold << line.back();
    if (italic) ss_italic << line.back();

    // Append the bold or italic text to the output
    if (bold && italic) ss << "<strong>" << ss_bold.str() << "</strong>";
    else if (bold) ss << "<strong>" << ss_bold.str() << "</strong>";
    else if (italic) ss << "<em>" << ss_italic.str() << "</em>";
    else continue;

    // Handle links
    size_t start = 0, end = 0;
    while ((end = line.find('(', start)) != std::string::npos) {
      size_t open = end + 1, close = end + 1;
      while ((close = line.find(')', close)) != std::string::npos && close - open > 2)
        close++;
      std::string url = line.substr(open, close - open);
      start = close + 1;
      ss << "[" << line.substr(0, end - start) << "](" << url << ")";
    }
  }

  return ss.str();
}
```
