            if (b[j] == c[k]) {
                found = true;
                break;
            }
        }
        if (!found) {
            c[i++] = b[j];
        }
    }

    // In ra các phần tử chung
    for (i = 0; i < n + m; i++) {
        if (c[i] != -1) {
            cout << c[i] << " ";
        }
    }

    return 0;
}
```

