std::string htmlize(const std::string& markdown) {
    // Markdown to HTML converter using regex and HTML tags
    std::vector<std::string> html_tags{
        "<h1>", "</h1>", 
        "<h2>", "</h2>", 
        "<h3>", "</h3>", 
        "<h4>", "</h4>", 
        "<h5>", "</h5>", 
        "<h6>", "</h6>", 
        "<b>", "</b>", 
        "<i>", "</i>",
        "<u>", "</u>"
    };

    std::regex markdown_tags{
        "\\[\\[.*?\\]\\]",
        std::regex_constants::ECMAScript | std::regex_constants::icase
    };

    // Replace markdown tags with HTML tags
    for (const auto& tag : html_tags) {
        std::string html_tag = tag;
        if (tag.size() == 2) {
            html_tag = "<" + tag + ">";
            html_tag += "&nbsp;";
            html_tag += "</" + tag + ">";
        }

        std::string replacement = "\\[" + tag + ".*?\\[" + tag + "\\]";
        markdown = std::regex_replace(markdown, markdown_tags, replacement);
    }

    return markdown;
}
```

