public class Solution {
    public boolean isPowerOfThree(int n) {
        // 1. 若n为0或负数，直接返回false
        if (n <= 0) {
            return false;
        }
        // 2. 初始化二分查找的左右边界和中间值
        int left = 1, right = Integer.MAX_VALUE, mid;
        // 3. 若n的值比右边界大，则缩小右边界；否则缩小左边界
        while (left < right) {
            mid = left + (right - left) / 2;
            if (mid > n) {
                right = mid;
            } else {
                left = mid + 1;
            }
        }
        // 4. 若n与左边界相等，则返回true；否则，返回false
        return left == n;
    }
}
