#include <string>
#include <iostream>
#include <regex>
using namespace std;

std::string markdownToHTML(const std::string& markdown_text)
{
    // Replace all links in the markdown text
    regex pattern1("((http|https)://[^\s]+)");
    string html_text = regex_replace(markdown_text, pattern1, "<a href=\"\\1\">\\1</a>");

    // Replace all bold and italics in the markdown text
    regex pattern2("(\\*(\\w+\\*))|(\\_([a-zA-Z0-9]+_))");
    html_text = regex_replace(html_text, pattern2, "<strong>\\1</strong>");

    // Replace all blockquotes in the markdown text
    regex pattern3("^>(.*)");
    html_text = regex_replace(html_text, pattern3, "<blockquote>\\1</blockquote>");

    // Replace all code blocks in the markdown text
    regex pattern4("`{3}([^`]*)`{3}");
    html_text = regex_replace(html_text, pattern4, "<pre><code>\\1</code></pre>");

    return html_text;
}

int main()
{
    std::string markdown_text = "This is a **bold** sentence with an _italic_ word. Here's a [link](https://www.example.com) to an external website. This is a blockquote:\n> This is a blockquote. Here's some code:\n```javascript\nconsole.log('Hello world!');\n```";
    std::string html_text = markdownToHTML(markdown_text);
    cout << html_text << endl;
}
```

