```c++
#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
    string word; // La palabra que estamos buscando
    int count = 0; // Contador de apariciones

    // Obtener la palabra que estamos buscando del usuario
    cout << "Escriba la palabra que desea buscar: ";
    getline(cin, word);

    // Abrir el archivo de texto para lectura
    ifstream file("file.txt");
    if (file.is_open()) {
        string line;
        while (getline(file, line)) {
            // Buscar la palabra en la línea actual
            size_t pos = line.find(word);
            while (pos != string::npos) {
                count++;
                pos = line.find(word, pos + 1);
            }
        }
        file.close();
    } else {
        cout << "No se pudo abrir el archivo." << endl;
        return 1;
    }

    // Mostrar el resultado
    cout << "La palabra " << word << " aparece " << count << " veces en el archivo." << endl;

    return 0;
}
