def lcs(X, Y):
    # Creamos una matriz para almacener el resultado
    # de nuestro algoritmo dinámico
    L = [[None]*(len(Y) + 1) for i in range(len(X) + 1)]

    # La diagonal principal de la matriz es siempre igual a 1
    for i in range(len(X) + 1):
        L[i][0] = 0
    for j in range(len(Y) + 1):
        L[0][j] = 0

    for i in range(1, len(X) + 1):
        for j in range(1, len(Y) + 1):
            # Si los elementos son iguales, se actualiza el valor
            # en la matriz con el valor anterior mas uno
            if X[i - 1] == Y[j - 1]:
                L[i][j] = L[i - 1][j - 1] + 1
            # Si no son iguales, se actualiza el valor con el máximo entre los dos valores adyacentes
            else:
                L[i][j] = max(L[i - 1][j], L[i][j - 1])

    return L[-1][-1]

