#include <algorithm>
#include <regex>

std::string markdown_to_html(const std::string& markdown) {
  std::string html;

  // Remove leading and trailing whitespace
  markdown = std::regex_replace(markdown, std::regex("^\\s+"), "");
  markdown = std::regex_replace(markdown, std::regex("\\s+$"), "");

  // Replace headers with HTML tags
  html += "<h1>" + markdown.substr(0, markdown.find("\n")) + "</h1>\n";
  markdown.erase(0, markdown.find("\n") + 1);

  while (!markdown.empty()) {
    std::size_t newline_pos = markdown.find("\n");
    if (newline_pos == std::string::npos) {
      newline_pos = markdown.size();
    }

    std::size_t asterisk_pos = markdown.find("*");
    if (asterisk_pos != std::string::npos && asterisk_pos < newline_pos) {
      html += "<em>";
      markdown.replace(asterisk_pos, 1, "");
    }

    asterisk_pos = markdown.find("*");
    if (asterisk_pos != std::string::npos && asterisk_pos < newline_pos) {
      html += "</em>";
      markdown.replace(asterisk_pos, 1, "");
    }

    html += markdown.substr(0, newline_pos) + "\n";
    markdown.erase(0, newline_pos + 1);
  }

  return html;
}

int main() {
  std::cout << markdown_to_html("## Hello World!\n\nThis is some *markdown* text.") << std::endl;
  return 0;
}
```

