```c++
#include <iostream>
#include <string>
#include <sstream>
#include <algorithm>

std::string markdown_to_html(const std::string& input)
{
    std::stringstream ss;
    ss << "<html><body>";
    for (char c : input)
    {
        switch (c)
        {
            case '#':
                ss << "<h1>";
                break;
            case '*':
                ss << "<em>";
                break;
            case '_':
                ss << "<i>";
                break;
            case '`':
                ss << "<code>";
                break;
            case '[':
                ss << "<a href=\"";
                break;
            case ']':
                ss << "\">";
                break;
            case '(':
                ss << "<blockquote>";
                break;
            case ')':
                ss << "</blockquote>";
                break;
            default:
                ss << c;
        }
    }
    ss << "</body></html>";
    return ss.str();
}

int main()
{
    std::string input = "### Title\n\nThis is a **bold** text with a _link_ to [Google](https://www.google.com/).";
    std::cout << markdown_to_html(input) << std::endl;
    return 0;
}
```

