std::string convertMarkdownToHTML(const std::string& markdown) {
    std::vector<std::pair<std::string, std::string>> replacements = {
        {"__", "<em>"},
        {"**", "<strong>"},
        {"* ", "<li>"},
        {"[", "<a href="],
        {"](", ")</a>"},
        {"[(", "<a href="},
        {")]", "</a>"},
        {"((", "<a href="},
        {"))", "</a>"},
        {"`", "<code>"}
    };

    std::string html;

    for (auto& replacement : replacements) {
        auto pos = markdown.find(replacement.first);
        while (pos != std::string::npos) {
            html += markdown.substr(0, pos);
            html += replacement.second;
            markdown.erase(0, pos + replacement.first.length());
            pos = markdown.find(replacement.first);
        }
    }

    return html + markdown;
}
```

