std::string convertMarkdownToHtml(const std::string& markdown) {
    // Egy regisztrált kereső segítségével keresünk minden HTML szövegező jelet
    // és megváltoztatjuk a megfelelő HTML jelet.
    std::string html = markdown;
    const std::regex markdownTagRegex(
        R"((?:^|\s)(?:(?:\#[a-zA-Z0-9_-]+)|(?:\#[a-zA-Z0-9_-]+\s[a-zA-Z0-9_-]+)|(?:\#[a-zA-Z0-9_-]+\s[a-zA-Z0-9_-]+\s[a-zA-Z0-9_-]+)))");
    const std::regex htmlTagRegex(R"((?<!\S)(?:(?:<[^>]+>)|(?:<\/[^>]+>)))");
    std::smatch markdownTagMatch;
    while (std::regex_search(html, markdownTagMatch, markdownTagRegex)) {
        std::string markdownTag = markdownTagMatch[0];
        // Cseréljük a markdown jelent a megfelelő HTML jelre
        html.replace(markdownTagMatch.position(), markdownTag.length(),
                     "<" + markdownTag.substr(1) + ">");
    }

    // Ugyanezt megismételjük a HTML jelek számára
    const std::regex htmlTagMatchRegex(
        R"((?:^|\s)(?:(?:<[^>]+>)|(?:<\/[^>]+>)))");
    const std::regex htmlMatchRegex(R"((?<!\S)(?:(?:\#[a-zA-Z0-9_-]+)|(?:\#[a-zA-Z0-9_-]+\s[a-zA-Z0-9_-]+)|(?:\#[a-zA-Z0-9_-]+\s[a-zA-Z0-9_-]+\s[a-zA-Z0-9_-]+)))");
    std::smatch htmlTagMatch;
    while (std::regex_search(html, htmlTagMatch, htmlTagMatchRegex)) {
        std::string htmlTag = htmlTagMatch[0];
        // Cseréljük a HTML jelent a megfelelő Markdown jelre
        html.replace(htmlTagMatch.position(), htmlTag.length(),
                     "#" + htmlTag.substr(1));
    }

    return html;
}
```

