```cpp
#include <string>
#include <regex>
#include <sstream>

std::string convertMarkdownToHTML(const std::string& markdown) {
  std::stringstream html;

  // Regex expressions for converting markdown to html
  std::regex h1("#(.+)");
  std::regex h2("##(.+)");
  std::regex bold("__(.+)__");
  std::regex italic("_(.+)_");
  std::regex link("\\[([^\]]+)\\]\\(([^\\)]+)\\)");
  std::regex codeblock("(`{3}([^`]+)`{3})");
  std::regex blockquote(">([^\n]+)");

  // Loop through each line in the markdown string
  std::istringstream iss(markdown);
  std::string line;
  while (std::getline(iss, line)) {
    // Check if the line matches the regex for a header
    if (std::regex_match(line, h1)) {
      html << "<h1>" << std::regex_replace(line, h1, "$1") << "</h1>";
    } else if (std::regex_match(line, h2)) {
      html << "<h2>" << std::regex_replace(line, h2, "$1") << "</h2>";
    } else if (std::regex_match(line, bold)) {
      html << "<strong>" << std::regex_replace(line, bold, "$1") << "</strong>";
    } else if (std::regex_match(line, italic)) {
      html << "<em>" << std::regex_replace(line, italic, "$1") << "</em>";
    } else if (std::regex_match(line, link)) {
      html << "<a href=\"" << std::regex_replace(line, link, "$2") 
           << "\">" << std::regex_replace(line, link, "$1") << "</a>";
    } else if (std::regex_match(line, codeblock)) {
      html << "<pre><code>" << std::regex_replace(line, codeblock, "$2") 
           << "</code></pre>";
    } else if (std::regex_match(line, blockquote)) {
      html << "<blockquote>" << std::regex_replace(line, blockquote, "$1") 
           << "</blockquote>";
    } else {
      html << line;
    }
    html << "\n";
  }

  return html.str();
}
```

