```c++
#include <string>
#include <vector>
#include <sstream>

std::string markdown_to_html(const std::string& markdown) {
    std::stringstream ss;
    // Definerer et array av mārkdown-tekstene og deres html-ekvivalenter
    std::vector<std::pair<std::string, std::string>> markdown_to_html = {
        {"##", "<h2>"},
        {"###", "<h3>"},
        {"####", "<h4>"},
        {"#####", "<h5>"},
        {"######", "<h6>"},
        {"*italic*", "<i>italic</i>"},
        {"**bold**", "<b>bold</b>"},
        {"~~strike through~~", "<s>strike through</s>"}
    };

    // Går gjennom alle paretene i arrayet
    for (const auto& pair : markdown_to_html) {
        // Hvis markdown-teksten finnes i markdown-inputen, erstatter vi den med html-teksten
        if (markdown.find(pair.first) != std::string::npos) {
            size_t pos = 0;
            while ((pos = markdown.find(pair.first, pos)) != std::string::npos) {
                ss << markdown.substr(pos, pair.first.length()) << pair.second;
                pos += pair.second.length();
            }
        }
    }

    // Returnerer den nye html-teksten
    return ss.str();
}
```

