std::string convert_markdown_to_html(const std::string& markdown_text) {
    std::string html_text;

    // Replace Markdown headings with HTML headings
    std::regex heading_regex("^#(.*)$", std::regex_constants::extended);
    std::smatch heading_match;
    if (std::regex_match(markdown_text, heading_match, heading_regex)) {
        html_text += "<h" + heading_match[1].length() + ">" + heading_match[1] + "</h" + heading_match[1].length() + ">";
    }

    // Replace Markdown lists with HTML lists
    std::regex list_regex("^\\s*-\\s*(.*)$", std::regex_constants::extended);
    std::smatch list_match;
    while (std::regex_search(markdown_text, list_match, list_regex)) {
        html_text += "<ul><li>" + list_match[1] + "</li></ul>";
        markdown_text = list_match.suffix();
    }

    // Replace Markdown links with HTML links
    std::regex link_regex("(!\\[(.*)\\]\\((.*)\\))", std::regex_constants::extended);
    std::smatch link_match;
    while (std::regex_search(markdown_text, link_match, link_regex)) {
        html_text += "<a href=\"" + link_match[3] + "\">" + link_match[2] + "</a>";
        markdown_text = link_match.suffix();
    }

    return html_text;
}
