def find_longest_common_subsequence(str1, str2):
    n = len(str1)
    m = len(str2)

    # create a matrix to store the LCS values
    lcs_matrix = [[0 for _ in range(m + 1)] for _ in range(n + 1)]

    # initialize the first row and column with 0
    for i in range(1, n + 1):
        lcs_matrix[i][0] = 0

    for j in range(1, m + 1):
