    std::string htmlText = ""; // Stores the converted HTML text
    std::stringstream ss(markdownText); // Convert the markdown text to a stringstream for easier processing
    std::map<std::string, std::string> replacements = {
        {"*", "<strong>"}, {"_", "<em>"}, {"[", "<a href=\""}, {"](", "\">"}, {"~", "<s>"} // Define replacement patterns for Markdown syntax
    }; // Replacement patterns for Markdown syntax
    std::string currentLine = ""; // Stores the current line being processed
    std::string replacement = ""; // Stores the replacement string for the current pattern being matched
    while (std::getline(ss, currentLine)) { // Read each line of the Markdown text
        for (auto& pair : replacements) { // Loop through each replacement pattern and replace in the current line
            replacement = pair.second; // Get the replacement string for the current pattern being matched
            size_t startPos = 0; // Start position of the current pattern in the current line
            while ((startPos = currentLine.find(pair.first, startPos)) != std::string::npos) { // Loop through all occurrences of the current pattern in the current line
                currentLine.replace(startPos, pair.first.length(), replacement); // Replace the pattern with the replacement string in the current line
