class Queue:
    def __init__(self):
        self.front_stack = []
        self.rear_stack = []

    def enqueue(self, item):
        self.rear_stack.append(item)

    def dequeue(self):
        if not self.front_stack:
            while self.rear_stack:
                self.front_stack.append(self.rear_stack.pop())
        return self.front_stack.pop() if self.front_stack else None
