    std::smatch match;
    while (std::regex_search(markdown, match, header_regex)) {
        int level = match.size() - 1; // Antal #-tegn angiver niveauet af overskriften
        html += "<h" + std::to_string(level) + ">" + match[1] + "</h" + std::to_string(level) + ">";
