def find_longest_common_substring(str1, str2):
    m = len(str1)
    n = len(str2)
    L = [[0] * (n + 1) for _ in range(m + 1)]
    longest, x_end, y_end = 0, 0, 0

    for i in range(1, m + 1):
        for j in range(1, n + 1):
            if str1[i-1] == str2[j-1]:
                L[i][j] = L[i-1][j-1] + 1
