def plsc(s1, s2):
    m = len(s1)
    n = len(s2)
    c = [[0] * (n+1) for _ in range(m+1)]
    b = [[None] * (n+1) for _ in range(m+1)]
    for i in range(1, m+1):
        for j in range(1, n+1):
            if s1[i-1] == s2[j-1]:
                c[i][j] = c[i-1][j-1] + 1
                b[i][j] = 'd'
            elif c[i-1][j] >= c[i][j-1]:
