from collections import deque

class Queue:
    def __init__(self):
        self.stack1 = deque()
        self.stack2 = deque()

    def enqueue(self, item):
        self.stack1.append(item)

    def dequeue(self):
        if not self.stack2:
            while self.stack1:
                self.stack2.append(self.stack1.pop())
        return self.stack2.pop()

    def is_empty(self):
        return len(self.stack1) == 0 and len(self.stack2) == 0
