int countOccurrences(const string& filename, const string& word) {
    int count = 0;
    ifstream file(filename);
    if (file.is_open()) {
        string line;
        while (getline(file, line)) {
            size_t pos = 0;
            while ((pos = line.find(word, pos)) != string::npos) {
                count++;
                pos++; // Move to the next occurrence of the word in the line
            }
        }
        file.close();
    } else {
        cout << "Unable to open file: " << filename << endl;
    }
    return count;
}
