```c++
#include <string>
#include <iostream>
#include <fstream>

std::string markdownToHtml(const std::string& markdown) {
    std::string html;
    std::ifstream input(markdown);
    if (input.is_open()) {
        std::string line;
        while (std::getline(input, line)) {
            html += "<p>" + line + "</p>";
        }
        input.close();
    } else {
        std::cerr << "Failed to open file." << std::endl;
    }
    return html;
}
```
